import http from "../http-common";
import authHeader from "./auth-header";
import PostService from "../../services/PostService";

const ratePost = () => {
    const currentPost = PostService.get();
    return http.post(`/likes/:id?postId=${currentPost.id}`, { headers: authHeader() });
};

const LikeService = {
    ratePost
};

export default LikeService;
