import http from "../http-common";
import authHeader from "./auth-header";

const like = id => {
    return http.post(`/likes/${id}`, { headers: authHeader() });
};
const dislike = id => {
    return http.delete(`/likes/${id}`)
}

const LikeService = {
    like,
    dislike
};

export default LikeService;
