import React from "react";
import { Link } from "react-router-dom";
import AuthService from "../services/auth.service";
const currentUser = {};
const Navbar = () => {
    return(
    currentUser ? (
        <nav className="nav_bar_connected">
        <ul className="ul_nav">
          <Link to="/" className="nav-link" onClick={AuthService.logout}>Se d√©connecter</Link>            
          <Link to={"/user"} className="nav-link">Utilisateur</Link>
          <Link to="/posts" className="nav-link">Accueil</Link>
          <Link to={"/add"} className="nav-link">Ajouter un Post</Link>
        </ul>
        </nav>
      ):(
        <nav className="nav_bar_notConnected">
        <ul className="ul_nav">
          <Link to={"/signin"} className="nav-link">Se connecter</Link>
          <Link to={"/register"} className="nav-link">S'enregistrer</Link>
        </ul>
        </nav>
    )
    );
}

export default Navbar;
