import React, { useState, useEffect} from "react";
import { useParams } from 'react-router-dom';
import LikeService from "../../services/like.service";
import AuthService from "../../services/auth.service";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { solid, regular} from '@fortawesome/fontawesome-svg-core/import.macro';

const RatePost = () => {
        const { id } = useParams();
        const currentUser = AuthService.getCurrentUser();
        const currentPostId = id;
        const [likes, setLikes] = useState("");
        const likesNumber =         
        function getLikes(){
            LikeService.getAll()
            .then((response) => {
                setLikes(response);
            })
        };
        useEffect(() => {
            setLikes();
          }, []);
        console.log(currentUser); 
        console.log(currentPostId);  
        console.log(likes);
    function VerifyRate() {
        function handleLike(e) {
            e.preventDefault();
            const like = {
                postId: currentPostId,//it is undifined need to know why
                userId: currentUser.id
            }
            LikeService.like(like); 
            window.location.reload();
        };
        if(likes.length === 0){
            return (                    
                <div className="container_like">
                <FontAwesomeIcon icon={regular("heart")} className="icon_heart" onClick={handleLike}/>                    
                <p className="number_of_like">{likes.length}</p>
                </div>
            );
        }        
        for (let i = 0; i < likes.length; i++) { 
            console.log(currentUser);         
            if(likes[i].userId === currentUser.id && likes[i].postId === currentPostId) {  
                return (                    
                    <div className="container_like">
                    <FontAwesomeIcon icon={solid("heart")} className="icon_heart" onClick={handleLike}/>                    
                    <p className="number_of_like">{likes.length}</p>
                    </div>
                );
            }else{
                return (                    
                    <div className="container_like">
                    <FontAwesomeIcon icon={regular("heart")} className="icon_heart" onClick={handleLike}/>                    
                    <p className="number_of_like">{likes.length}</p>
                    </div>
                );
            }
        };
    };
    return (
            <VerifyRate/>
    );
};
export default RatePost;
