import React, { useState, useEffect} from "react";
import { useParams } from 'react-router-dom';
import LikeService from "../services/like.service";
import AuthService from "../services/auth.service";
import PostService from "../services/PostService";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

const RatePost = () => {
    const { id }= useParams();
    const currentUser = AuthService.getCurrentUser();
    const [currentPost, setCurrentPost] = useState("");
    const likes = LikeService.getAll();
    const [message, setMessage] = useState("");
    const getPost = id => {
        PostService.get(id)
        .then(response => {
            setCurrentPost(response.data);
        })
        .catch(e => {
            console.log(e);
        });
    };
    useEffect(() => {
        if(id)
            getPost(id);
    }, [id]);
    const handleLike = (e) => {
        setMessage("");
        e.preventDefault();
        const like = {
            postId: currentPost.id,
            userId: currentUser.id,
            likes: +1
        }
        LikeService.like(like)
            .then((response) =>{
                setMessage(response.data.message)
            },
            (error) => {
                const resMessage =
                (error.response &&
                error.response.data &&
                error.response.data.message) ||
                error.message ||
                error.toString();
                setMessage(resMessage);
            }
        )
    };
    for (let i = 0; i < likes.length; i++) {
        if (likes[i].userId === currentUser.id) {
            return (
                <FontAwesomeIcon icon="fa-regular fa-heart" className="text-xl text-red" onClick={handleLike}/>
            );
        } else {
            return (
                <FontAwesomeIcon icon="fa-solid fa-heart" className="text-xl text-red" onClick={handleLike}/>
            );
        }
    }
};
export default RatePost;
