import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import ComServices from "../../services/com.service";
import "../../styles/com.css";

const Com = ({ id }) => {
    useEffect(() => {
        getComs();
    }, []);
    function getComs() {
        ComServices.getAll(id);
    }
    return (
        <div className="container_com flex">
            <label className="container_com flex" htmlFor="description">
                Commenter :
                <textarea type="text" className="txt_com" />
            </label>
            <p>auteur</p>
            <p>commentaire</p>
        </div>
    );
};

export default Com;
