import React, { useState, useEffect} from "react";
import { useParams } from 'react-router-dom';
import LikeService from "../../services/like.service";
import AuthService from "../../services/auth.service";
import PostService from "../../services/PostService";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { solid, regular} from '@fortawesome/fontawesome-svg-core/import.macro';

const RatePost = () => {
    const { id } = useParams();
    const currentUser = AuthService.getCurrentUser();    
    const [currentPost, setPost] = useState();
    const [likes, setLikes] = useState([]);
    const VerifyRate = () =>{
    const retriveLikes = () => {
        LikeService.getAll()
          .then(response => {
            setLikes(response);
          })
          .catch(e => {
            console.log(e);
          });
    };  
    useEffect(() => {
        retriveLikes();
    }, []);
    const retrivePost = () => {
        PostService.get(id)
          .then(response => {
            setPost(response);
          })
          .catch(e => {
            console.log(e);
          });
    };  
    useEffect(() => {
        retrivePost();
    }, []);
        const handleLike = (e) => {
            e.preventDefault();
            const like = {
                postId: currentPost.id,//it is undifined need to know why
                userId: currentUser.id,
            }
            LikeService.like(like); 
            window.location.reload();
        };
        for (let i = 0; i < likes.length; i++) {          
            if(likes[i].userId === currentUser.id && likes[i].postId === currentPost.id) {  
                return (
                    <FontAwesomeIcon icon={solid("heart")} className="icon_empty_heart" onClick={handleLike}/>
                );
            }else{
                return (
                    <FontAwesomeIcon icon={regular("heart")} className="icon_heart" onClick={handleLike}/>
                );
            }
        };
        if(likes.userId !== currentUser.id){
            return (
                <FontAwesomeIcon icon={regular("heart")} className="icon_heart" onClick={handleLike}/>
            );
        };
    };
    return (
        <div className="container_like">
            <VerifyRate/>
            <p className="number_of_like">{likes.length}</p>
        </div>
    );
};
export default RatePost;
