import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from 'react-router-dom';
import PostDataService from "../services/PostService";
const Post = props => {
  const { id }= useParams();
  let navigate = useNavigate();
  const initialPostState = {
    id: null,
    title: "",
    description: ""
  };
  const [currentPost, setCurrentPost] = useState(initialPostState);
  const [message, setMessage] = useState("");
  const getPost = id => {
    PostDataService.get(id)
      .then(response => {
        setCurrentPost(response.data);
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  };
  useEffect(() => {
    if (id)
      getPost(id);
  }, [id]);
  const handleInputChange = event => {
    const { name, value } = event.target;
    setCurrentPost({ ...currentPost, [name]: value });
  };
  const updatePublished = status => {
    var data = {
      id: currentPost.id,
      title: currentPost.title,
      description: currentPost.description
    };
    PostDataService.update(currentPost.id, data)
      .then(response => {
        setCurrentPost({ ...currentPost});
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  };
  const updatePost = () => {
    PostDataService.update(currentPost.id, currentPost)
      .then(response => {
        console.log(response.data);
        setMessage("Le Post à bien été modifié");
      })
      .catch(e => {
        console.log(e);
      });
  };
  const deletePost = () => {
    PostDataService.remove(currentPost.id)
      .then(response => {
        console.log(response.data);
        navigate("/posts");
      })
      .catch(e => {
        console.log(e);
      });
  };
  return (
    <div>
      {currentPost ? (
        <div className="edit-form">
          <h4>Post</h4>
          <form>
            <div className="form-group">
              <label htmlFor="title">Titre</label>
              <input
                type="text"
                className="form-control"
                id="title"
                name="title"
                value={currentPost.title}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="description">Description</label>
              <input
                type="text"
                className="form-control"
                id="description"
                name="description"
                value={currentPost.description}
                onChange={handleInputChange}
              />
            </div>
          </form>
          <button className="badge badge-danger mr-2" onClick={deletePost}>
            Supprimer
          </button>
          <button
            type="submit"
            className="badge badge-success"
            onClick={updatePost}
          >
            Modifier
          </button>
          <p>{message}</p>
        </div>
      ) : (
        <div>
          <br />
          <p>Please click on a Tutorial...</p>
        </div>
      )}
    </div>
  );
};
export default Post;
