import React, { useState, useRef } from "react";
import { Redirect } from "react-router";
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";
import { isEmail } from "validator";
import AuthService from "../../services/auth.service";
import '../../styles/Auth.css'

const required = (value) => {
  if (!value) {
    return (
      <div className="alert alert-danger" role="alert">
        This field is required!
      </div>
    );
  }
};
const validEmail = (value) => {
  if (!isEmail(value)) {
    return (
      <div className="alert alert-danger" role="alert">
        This is not a valid email.
      </div>
    );
  }
};
const vpassword = (value) => {
  if (value.length < 6 || value.length > 40) {
    return (
      <div className="alert alert-danger" role="alert">
        The password must be between 6 and 40 characters.
      </div>
    );
  }
};
const Register = () => {    
  let navigate = useNavigate();
  const form = useRef();
  const checkBtn = useRef();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [successful, setSuccessful] = useState(false);
  const [message, setMessage] = useState("");
  const onChangeEmail = (e) => {
    const email = e.target.value;
    setEmail(email);
  };
  const onChangePassword = (e) => {
    const password = e.target.value;
    setPassword(password);
  };
  const handleRegister = (e) => {
    e.preventDefault();
    setMessage("");
    setSuccessful(false);
    form.current.validateAll();
    if (checkBtn.current.context._errors.length === 0) {
      AuthService.register(email, password).then(
        (response) => {
          setMessage(response.data.message);
          setSuccessful(true);
          window.location.reload();
        },
        (error) => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();
          setMessage(resMessage);
          setSuccessful(false);
        }
      );
    }else {
      setSuccessful(true);
    }
  };
  return (
    <div className='container_form'>        
        <Form className="form_signup" onSubmit={handleRegister} ref={form}>
          {!successful && (
            <div className='container_signup'>
              <img src="//ssl.gstatic.com/accounts/ui/avatar_2x.png" alt="profile-img" className="profile_img"/>              
              <label htmlFor="email">E-mail
              <Input
                type="text"
                className="input_signup"
                name="email"
                value={email}
                onChange={onChangeEmail}
                validations={[required, validEmail]}
              /></label>
              <label htmlFor="password">Mot de passe
              <Input
                type="password"
                className="input_signup"
                name="password"
                value={password}
                onChange={onChangePassword}
                validations={[required, vpassword]}
              /></label>
              <button type="submit" className="btn_signup">S'enregistrer</button>
            </div>
          )}
          {message && (
            <div className="form-group">
              <div
                className={ successful ? "alert alert-success" : "alert alert-danger" }
                role="alert"
              >
                {message}
              </div>
            </div>
          )}
          <CheckButton style={{ display: "none" }} ref={checkBtn} />
        </Form>
    </div>
  );
};
export default Register;
