import http from "../http-common";
import authHeader from "./auth-header";
const getAll = () => {
    return http.get("/likes", { headers: authHeader() });
}
const like = data => {
    return http.post(`/likes`, data, { headers: authHeader() });
};
const dislike = id => {
    return http.delete(`/likes/${id}`, { headers: authHeader() });
}

const LikeService = {
    getAll,
    like,
    dislike
};

export default LikeService;
