import React, { useState } from "react";
import PostDataService from "../../services/PostService";
import AuthService from "../../services/auth.service";
import '../../styles/AddPost.css';

const AddPost = () => {  
  const currentUser = AuthService.getCurrentUser();
  const [post, setPost] = useState("");

  function handleInputChange(event) {
    const { name, value } = event.target;
    setPost({ ...post, [name]: value });
  };
  function savePost() {
    var data = {
      author: currentUser.id,
      title: post.title,
      description: post.description
    };
    PostDataService.create(data)
      .then(response => {
        setPost({
          author: response.data.author,
          title: response.data.title,
          description: response.data.description
        });
      })
      .catch(e => {
        console.log(e);
      });
  };
  return (
    <div className="container_addpost">
      <h1>Cr√©er un Post</h1>
      <div className="container_item_addpost">
        <label className="label_addpost" htmlFor="title">Titre:</label>
        <input
          type="text"
          className="input_addpost"
          id="title"
          required
          value={post.title}
          onChange={handleInputChange}
          name="title"
        />          
        <label className="label_addpost" htmlFor="description">Description:</label>
        <input
          type="text"
          className="input_addpost"
          id="description"
          required
          value={post.description}
          onChange={handleInputChange}
          name="description"
        />        
      </div>
      <button onClick={savePost} className="btn_addpost">
        Ajouter le post
      </button>
    </div>
  );
};
export default AddPost;
