import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from 'react-router-dom';
import PostDataService from "../../services/PostService";
import AuthService from "../../services/auth.service";
import Like from "../like";
import '../../styles/Post.css'

const Post = () => {
  const currentUser = AuthService.getCurrentUser();
  const { id }= useParams();
  let navigate = useNavigate();
  const initialPostState = {
    id: null,
    title: "",
    description: ""
  };
  const [currentPost, setCurrentPost] = useState(initialPostState);
  const [message, setMessage] = useState("");
  const getPost = id => {
    PostDataService.get(id)
      .then(response => {
        setCurrentPost(response.data);
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  };
  useEffect(() => {
    if (id)
      getPost(id);
  }, [id]);
  const handleInputChange = event => {
    const { name, value } = event.target;
    setCurrentPost({ ...currentPost, [name]: value });
  };
  const updatePost = () => {
    PostDataService.update(currentPost.id, currentPost)
      .then(response => {
        console.log(response.data);
        setMessage("Le Post à bien été modifié");
      })
      .catch(e => {
        console.log(e);
      });
  };
  const deletePost = () => {
    PostDataService.remove(currentPost.id)
      .then(response => {
        console.log(response.data);
        navigate("/posts");
      })
      .catch(e => {
        console.log(e);
      });
  };
  return (     
    <div className="container_post">
      {currentUser.id === currentPost.author ?(
        <div className="content_modify_post"> 
          <h1 className="title_modify_post">Modification de votre Post</h1>
          <form className="form_modify_post">
              <label className="label_modify_post" htmlFor="title">Titre:</label>
              <input
                type="text"
                className="input_modify_post"
                id="title"
                name="title"
                value={currentPost.title}
                onChange={handleInputChange}
              />
              <label className="label_modify_post" htmlFor="description">Description:</label>
              <input
                type="text"
                className="input_modify_post"
                id="description"
                name="description"
                value={currentPost.description}
                onChange={handleInputChange}
              />
              <Like/>
          </form>
          <div>
            <button className="badge badge-danger mr-2" onClick={deletePost}>
              Supprimer
            </button>
            <button
              type="submit"
              className="badge badge-success"
              onClick={updatePost}>
                Modifier
            </button>
            <p>{message}</p>   
          </div>        
        </div>   
      ):(
        <div className="content_post">
          <h1 className="title_post">{currentPost.title}</h1>
          <form className="form_modify_post">
            <p>
             {currentPost.description}
            </p>
            <Like/>
          </form>      
        </div>
      )}      
    </div>
  );
};
export default Post;
