import React, { useState, useEffect} from "react";
import { useParams } from 'react-router-dom';
import LikeService from "../../services/like.service";
import AuthService from "../../services/auth.service";
import PostService from "../../services/PostService";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { solid, regular} from '@fortawesome/fontawesome-svg-core/import.macro';

const RatePost = () => {
    
    function VerifyRate() {
        const { id } = useParams();
        const currentUser = AuthService.getCurrentUser();
        const currentPostId = id;
        const likes = new Promise(
            function getLikes(){
                LikeService.getAll()
                .then(response => { return response;})
        });
        
        console.log(currentUser); 
        console.log(currentPostId);  
        console.log(likes);
        function handleLike(e) {
            e.preventDefault();
            const like = {
                postId: currentPostId,//it is undifined need to know why
                userId: currentUser.id
            }
            LikeService.like(like); 
            window.location.reload();
        };
        if(likes.length === 0){
            return (
                <FontAwesomeIcon icon={regular("heart")} className="icon_heart" onClick={handleLike}/>
            );
        }        
        for (let i = 0; i < likes.length; i++) { 
            console.log(currentUser);         
            if(likes[i].userId === currentUser.id && likes[i].postId === currentPostId) {  
                return (
                    <FontAwesomeIcon icon={solid("heart")} className="icon_empty_heart" onClick={handleLike}/>
                );
            }else{
                return (
                    <FontAwesomeIcon icon={regular("heart")} className="icon_heart" onClick={handleLike}/>
                );
            }
        };
    };
    return (
        <div className="container_like">
            <VerifyRate/>
            <p className="number_of_like"></p>
        </div>
    );
};
export default RatePost;
