import React from "react";
import { Routes, Route, Link, } from "react-router-dom";
//import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import logo from './GroupomaniaLogos/icon-left-font.svg'
import AuthService from "./services/auth.service";
import Login from "./components/auth/Login";
import Register from "./components/auth/Register";
import Home from "./components/Home";
import Profile from "./components/Profile";
import AddPost from "./components/post/AddPost";
import Post from "./components/post/Post";
import PostList from "./components/post/PostList";

function App() {  
  const currentUser = AuthService.getCurrentUser();
  const logOut = AuthService.logout();
  return (
    <div className='container_app'>
      <img className='logo_banner' src={logo} alt='Logo de Groupomania' />      
        {currentUser ? (
          <nav className="nav_bar_connected">
          <ul className="ul_nav">
            <Link to="/" className="nav-link" onClick={logOut}>Se déconnecter</Link>            
            <Link to={"/user"} className="nav-link">Utilisateur</Link>
            <Link to="/posts" className="nav-link">Accueil</Link>
            <Link to={"/posts"} className="nav-link">Posts</Link>
            <Link to={"/add"} className="nav-link">Ajouter un Post</Link>
          </ul>
          </nav>
        ):(
          <nav className="nav_bar_notConnected">
          <ul className="ul_nav">
            <Link to={"/login"} className="nav-link">Login</Link>
            <Link to={"/register"} className="nav-link">Sign Up</Link>
          </ul>
          </nav>
        )}
      <Routes>
        <Route path="/" element={<Home/>} />
        <Route path="/home" element={<Home/>} />
        <Route path="/login" element={<Login/>} />
        <Route path="/register" element={<Register/>} />
        <Route path="/profile" element={<Profile/>} />
        <Route path="/posts" element={<PostList/>} />
        <Route path="/add" element={<AddPost/>} />
        <Route path="/posts/:id" element={<Post/>} />
      </Routes>
      <footer>
        <ul className='ul_footer'>
          <li className='li_footer'>Nous Contacter</li>
          <li className='li_footer'>Conditions générales</li>
          <li className='li_footer'>Confidentialité</li>
        </ul>
      </footer>
    </div>
  );
}
export default App;
