import React, { useState, useEffect} from "react";
import { useParams } from 'react-router-dom';
import LikeService from "../services/like.service";
import AuthService from "../services/auth.service";
import PostService from "../services/PostService";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { solid, regular} from '@fortawesome/fontawesome-svg-core/import.macro';

const RatePost = () => {
    const { id }= useParams();
    const currentUser = AuthService.getCurrentUser();
    const [currentPost, setCurrentPost] = useState("");
    const [currentLike, setCurrentLike] = useState("");
    const [likes, setLikes] = useState("");
    const getLikes = () => {
        LikeService.getAll()
        .then(response =>{
            setLikes(response.data);
        })
        .catch(e => {
            console.log(e);
        });
    };
    useEffect(() => {
        getLikes()
    },[]);
    const getPost= () => {
        PostService.get(id)
        .then(response => {
            setCurrentPost(response.data);
        })
        .catch(e => {
            console.log(e);
        });
    };
    useEffect(() => {
        getPost()
    },[]);
    const VerifyRate = () =>{
        const handleLike = (e) => {
            e.preventDefault();
            const like = {
                postId: currentPost.id,
                userId: currentUser.id,
            }
            LikeService.like(like)
                .then((response) =>{ 
                    setCurrentLike(response.data);
                })
                .catch((error) => {
                    (error.response &&
                    error.response.data &&
                    error.response.data.message) ||
                    error.message ||
                    error.toString();
                });

            if(currentLike.userId === currentUser.id) {  
                return (
                    <FontAwesomeIcon icon={solid("heart")} className="icon_empty_heart" onClick={handleLike}/>
                );
            }else{
                return (
                    <FontAwesomeIcon icon={regular("heart")} className="icon_heart" onClick={handleLike}/>
                );
            }        
        };
    };
    return (
        <div className="container_like">
            <VerifyRate/>
            <p className="number_of_like">{likes.length}</p>
        </div>
    );
};
export default RatePost;
